import React, { useState } from 'react'
import FeatherIcon from "feather-icons-react";
import { DropdownMenu, DropdownToggle, UncontrolledDropdown } from 'reactstrap';

export default function TreeInput({ data = [] }) {
    // const [treeValue, setTreeValue] = useState("party");
    return (

        <UncontrolledDropdown>
            <DropdownToggle type="button" className="btn btn-info">
                {"TreeValue"} <i className="mdi mdi-chevron-down"></i></DropdownToggle>
            <DropdownMenu  className="dropdownmenu-info">
                <Tree data={data}/>
            </DropdownMenu>
        </UncontrolledDropdown>

    )
}

export  function Tree({ data = [] }) {
    return (

        <div>
            <div >

                <ul  >
                    {data.map((tree) => (
                        <TreeNode node={tree} />
                    ))}

                </ul>
            </div>
        </div>
    )
}

const TreeNode = ({ node }) => {
    const [childVisible, setchildVisible] = useState(false);
    const haschild = node.childern ? true : false;
    return (
        <li >
            <div className='d-flex' onClick={e => {
                // setTreeValue(node.Name)
                setchildVisible(v => !v)}}>
                {haschild && (
                    <div className={`d-inline d-tree-toggler ${childVisible ? "active" : ''}`}>
                        <i class=" fa-edit me-2"></i>
                    </div>
                )}
                <div className='col d-tree-head'>
                    {/* <i className={`mr-1${"fa fa-file"}`}/> */}
                    {node.Name}
                </div>
            </div>
            {
                haschild && childVisible && <div >
                    <ul >
                        <Tree data={node.childern} />
                    </ul>
                </div>
            }
        </li>
    )

}
