import React, { useState } from "react";
import { Button, Table } from "reactstrap";
import { Tbody, Thead } from "react-super-responsive-table";
import { deleteID_In_MasterPage } from "../../../../../store/Administrator/MRPMasterRedux/action";
import { AlertState } from "../../../../../store/actions";
import { useDispatch, useSelector } from "react-redux";

function MRPTable(props) {
  const dispatch = useDispatch();
  //Access redux store Data /  'save_ModuleSuccess' action data
  const {
    deleteMessage,
  } = useSelector((state) => ({
    deleteMessage: state.MRPMasterReducer.deleteIdForMRPMaster,
  }));
  const onDeleteHandeler = (ele) => {
    // if (!(ele === 0)) {
    //   var fil = props.tableData.filter((i) => {
    //     return !(i.id === ele);
    //   });
    //   props.func(fil);
    // }

    dispatch(
      AlertState({
        Type: 5,
        Status: true,
        Message: `Are you sure you want to delete this MRP"`,
        RedirectPath: false,
        PermissionAction: deleteID_In_MasterPage,
        ID: ele.id,
      })
    );
  };

  useEffect(() => {
    if (deleteMessage.Status === true && deleteMessage.StatusCode === 200) {
      dispatch(deleteID_In_MasterPageSuccess({ Status: false }));

      if (!(ele === 0)) {
        var fil = props.tableData.filter((i) => {
          return !(i.id === ele);
        });
        props.func(fil);
      }

      dispatch(
        AlertState({
          Type: 1,
          Status: true,
          Message: deleteMessage.Message,
        })
      );
    } else if (deleteMessage.Status === true) {
      dispatch(deleteID_In_MasterPageSuccess({ Status: false }));
      dispatch(
        AlertState({
          Type: 3,
          Status: true,
          Message: JSON.stringify(deleteMessage.Message),
        })
      );
    }
  }, [deleteMessage]);

  const tableRows = props.tableData.map((info) => {
    return (
      <tr>
        {/* <td>{info.id}</td> */}
        <td>{info.DivisionName}</td>
        <td>{info.PartyName}</td>
        <td>{info.EffectiveDate}</td>
        <td>{info.MRP}</td>
        <td>
          <Button
            className="badge badge-soft-danger font-size-12 btn btn-danger waves-effect waves-light w-xxs border border-light"
            data-mdb-toggle="tooltip"
            data-mdb-placement="top"
            title="Delete Party Type"
            onClick={(e) => {
              onDeleteHandeler(info.id);
            }}
          >
            <i className="mdi mdi-delete font-size-18"></i>
          </Button>
        </td>
      </tr>
    );
  });

  return (
    <>
      <div>
        {props.tableData.length > 0 ? (
          <Table className="table table-bordered table-hover">
            <Thead>
              <tr>
                <th className="col col-sm-3">Division</th>
                <th className="col col-sm-3">Party Name</th>
                <th className="col col-sm-3">Effective Date</th>
                <th className="col col-sm-3">MRP</th>
                <th className="col col-sm-3">{"Action"}</th>
              </tr>
            </Thead>
            <Tbody>{tableRows}</Tbody>
          </Table>
        ) : null}
      </div>
    </>
  );
}

export default MRPTable;
