import React, { useState } from 'react'
import FeatherIcon from "feather-icons-react";
import { Col, Dropdown, DropdownMenu, DropdownToggle, Input, Label, Modal, Row, UncontrolledDropdown } from 'reactstrap';

export default function TreeInput({ data = [] }) {
    // const [treeValue, setTreeValue] = useState("party");
    const [dropOpen, setDropOpen] = useState(false);
    let aa = false
    function funOnChange() {
        setDropOpen(!dropOpen)
    }

    function fun1(data1) {
        return (

            <div>

                {
                    data1.map(tree => fun2(tree))
                }
            </div>



        )
    }

    function fun2(data2) {
        return (
            // <li >
            <div style={{ paddingLeft: "20px" }} className={"mt-n1"} >
                <div className='row justify-content-center '>
                    <div className=' col-10'>
                        <Input type="text" defaultValue={data2.Name} ></Input>

                        {/*  */}
                    </div>
                    <div className=' col-1 al-end'> <input type="checkBox" ></input></div>
                    <div className=' col-1 '>  <i className="mdi mdi-pencil font-size-12" onClick={e=>funOnChange(data2)}
                     ></i>
                      <Dropdown isOpen={false} toggle={"toggle"} className="d-inline-block">
        <DropdownToggle className="btn header-item " tag="button">
          {/* <img
            src={get(languages, `${selectedLang}.flag`)}
            alt="Skote"
            height="16"
            className="me-1"
          /> */}
        </DropdownToggle>
        <DropdownMenu className="language-switch dropdown-menu-end">
          {/* {map(Object.keys(languages), key => (
            <DropdownItem
              key={key}
              onClick={() => changeLanguageAction(key)}
              className={`notify-item ${selectedLang === key ? "active" : "none"
                }`}
            >
              <img
                src={get(languages, `${key}.flag`)}
                alt="Skote"
                className="me-1"
                height="12"
              />
              <span className="align-middle">
                {get(languages, `${key}.label`)}
              </span>
            </DropdownItem>
          ))} */}<Input></Input>
          <Input></Input>
          <Input></Input>
        </DropdownMenu>
      </Dropdown>
                    </div>

                    {data2.childern ? fun1(data2.childern) : null}
                </div>
            </div>

        )

    }



    return (

        <div>
            <Modal
                isOpen={false}
                // toggle={() => { tog_center() }}
                size="modal-fullscreen-xxl-down"
            >
                <div>
                    <Row className='px-md-4 pt-3 '>

                        <div className='row'>
                            <Label className='col col'>Category</Label>
                            {/* <p>jfhgv</p> */}
                            <div className='col'>
                                <Dropdown
                                    className="d-none d-lg-inline-block "
                                    isOpen={dropOpen}
                                    toggle={() => {
                                        // setsocialDrp(!socialDrp)
                                    }}
                                >
                                    <DropdownToggle
                                        className="btn header-item  text-danger"
                                        tag="button"
                                    >
                                        {/* <FeatherIcon
                                    icon="edit"
                                    className="icon-pencile"
                                /> */}
                                    
                                    </DropdownToggle>
                                    <DropdownMenu className="dropdown-menu-lg dropdown-menu-end"></DropdownMenu>
                                </Dropdown>
                            </div>
                        </div>

                    </Row>
                    <Row className='px-4'>
                    </Row>
                </div>

            </Modal>
                        {fun1(data)}

        </div>


    )
}

