import { AlertState, socialLogin } from "../../../store/actions";
import { basicAmount, GstAmount, handleKeyDown, totalAmount } from "./OrderPageCalulation";

let description = 'order'

function Order() {

    const props = { tableData: [], func: function a() { } }
