import React, { useState } from "react";
import { Button, Table } from "reactstrap";
import { Tbody, Thead } from "react-super-responsive-table";

function MRPTable(props) {
    
  const dispatch = useDispatch();
 
  const { Party, Division } = useSelector((state) => ({
      Division: state.ItemMastersReducer.Division,
      Party: state.ItemMastersReducer.Party,
  }));

  useEffect(() => {
      dispatch(get_Division_ForDropDown());
      dispatch(get_Party_ForDropDown());
  }, [dispatch]);

  const Party_DropdownOptions = Party.map((data) => ({
      value: data.id,
      label: data.Name,
  }));

  const Division_DropdownOptions = Division.map((data) => ({
      value: data.id,
      label: data.Name,
  }));

  const ondeleteHandeler = (ele) => {
     
          var fil = props.tableData.filter((i) => {
              return !(i.id === ele.id);
          });
          props.func(fil);
     
  };
  const partyOnchange=(e,row)=>{
      row["PartyName"] = e.label
      row["Party"] = e.value;
      row["alert"]=true
      
  }

  const options = [
      { value: 'active', label: 'Active' },
      { value: 'inactive', label: 'In Active' },
      { value: 'deleted', label: 'Delete' },
  ];
  const pagesListColumns = [
      {
          text: "DivisionName",
          dataField: "DivisionName",
          sort: true,
          formatter: (value, row) => (
              <span className={`${row.alert?"text-warning":null}`}>
                {value}
              </span>
            )
      },
      {
          text: "PartyName",
          dataField: "PartyName",
          sort: true,
          formatter: (value, row) => (
              <div style={{ backgroundColor: "#FFFAFA" }}>
              <span className={`${row.alert?"text-info":null}`}>
                {value}
              </span>
              </div>
            ),

          editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (
              <>
                                     <Select
                                                                      defaultValue={options[0]}
                                                                      isSearchable={false}
                                                                      className="react-dropdown"
                                                                      // onChange={(v, e) => onChangeSelect({ e, v, state, setState })}
                                                                      classNamePrefix="dropdown"
                                                                      options={Party_DropdownOptions}
                                                                      name="Party"
                                                                      styles={{
                                                                          control: base => ({
                                                                              ...base,
                                                                              // border: isError.Address.length > 0 ? '1px solid red' : '',

                                                                          })
                                                                      }}
                                                                      onChange={(e)=>partyOnchange(e,row)}
                                                                  />
              </>


          )
      },


      {
          text: "EffectiveDate",
          dataField: "EffectiveDate",
          sort: true,

          editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (
              // <QualityRanger { ...editorProps } value={ value } />
              <>
                  {/* <Input type="checkbox" />
                  <Input type="text" onChange={(e)=>{debugger}}>

                  </Input> */}
                <Flatpickr
                                                                      name="DOB"
                                                                      className="form-control d-block p-2 bg-white text-dark"
                                                                      placeholder="YYYY-MM-DD"
                                                                      autoComplete="0,''"
                                                                      options={{
                                                                          altInput: true,
                                                                          altFormat: "F j, Y",
                                                                          dateFormat: "Y-m-d",
                                                                          minDate: new Date().fp_incr("n"),
                                                                          maxDate: new Date().fp_incr(0) // 14 days from now"0,''"
                                                                      }}
                                                                      // onChange={(y, v, e) => { onChangeDate({ e, v, state, setState }) }}
                                                                  />
              </>


          )
      },

      {
          text: "MRP",
          dataField: "MRP",
          sort: true,
          //   editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (

          // <QualityRanger { ...editorProps } value={ value } />
          //   )
      },
      {
          text: "Action",
          dataField: "MRP",
          sort: true,
         
          editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (
             
             <Button onClick={(e)=>ondeleteHandeler(row)} >delete</Button>
          )
         
      },

      
   
  ];
  const defaultSorted = [
      {
          dataField: "Name", // if dataField is not match to any column you defined, it will be ignored.
          order: "asc", // desc or asc
      },
  ];

  const pageOptions = {
      sizePerPage: 10,
      totalSize: props.tableData.length,
      custom: true,
  };

  return (
      <PaginationProvider pagination={paginationFactory(pageOptions)}>
                      {({ paginationProps, paginationTableProps }) => (
                          <ToolkitProvider
                              keyField="id"
                              defaultSorted={defaultSorted}
                              data={props.tableData}
                              columns={pagesListColumns}
                              search
                          >
                              {(toolkitProps) => (
                                  <React.Fragment>
                                      
                                      <Row>
                                          <Col xl="12">
                                              <div className="table">
                                                  <BootstrapTable
                                                      keyField={"id"}
                                                      responsive
                                                      bordered={false}
                                                      striped={false}
                                                      cellEdit={cellEditFactory({ mode: 'click', blurToSave: true })}
                                                      classes={"table  table-bordered"}
                                                      {...toolkitProps.baseProps}
                                                      {...paginationTableProps}
                                                  />
                                              </div>
                                          </Col>
                                      </Row>
                                      <Row className="align-items-md-center mt-30">
                                          <Col className="pagination pagination-rounded justify-content-end mb-2">
                                              <PaginationListStandalone {...paginationProps} />
                                          </Col>
                                      </Row>
                                  </React.Fragment>
                              )}
                          </ToolkitProvider>
                      )}
                   
                  </PaginationProvider> 
  )
}
export default MRPTable
