import React from 'react'
import { Input } from 'reactstrap'

export default function CInput(props) {
    
    function function1(e) {
       
        try {
            if (props.required) {
                if (!(props.validate === e.target.value)) {
                    document.getElementById(props.id).className = "form-control is-invalid"
                }
                else {
                    document.getElementById(props.id).className = "form-control"
                 }
            }
        } catch (e) {
            window.alert(e)
        }
        return props.onChange
    }
    document.hasFocus

    return (
        <Input
            id={props.id}
            value={props.value}
            type={props.type}
            placeholder={props.placeholder}
            autoComplete={props.autoComplete}
            validate={props.validate}
            // required={props.required}
            onChange={(e) => { function1(e) }}
        />
    )
}
