import React, { useState } from 'react'
import FeatherIcon from "feather-icons-react";
import { Col, Dropdown, DropdownMenu, DropdownToggle, Input, Label, Modal, Row, UncontrolledDropdown } from 'reactstrap';

export default function TreeInput({ data = [] }) {
    // const [treeValue, setTreeValue] = useState("party");
    let aa=false

    function fun1(data1) {
        return (

            <div>
                {
                    data1.map(tree => fun2(tree))
                }
            </div>



        )
    }

    function fun2(data2) {
        return (
            // <li >
            <div style={{ paddingLeft: "20px" }} >
                <div className='row justify-content-center mt-1'>
                    <div className=' col-10'>
                        <Input type="text" defaultValue={data2.Name} ></Input>

                        {/*  */}
                    </div>
                    <div className=' col-1 '> <input type="checkBox" ></input></div>
                    <div className=' col-1 '>     
                     <Dropdown>
                <DropdownToggle type="button" className="btn btn-info">
                    Info <i className="mdi mdi-chevron-down"></i></DropdownToggle>
                <DropdownMenu className="dropdownmenu-info"></DropdownMenu>
                </Dropdown>
                </div>

                    {data2.childern ? fun1(data2.childern) : null}
                </div>
            </div>

        )

    }



    return (

        <div>
            <Modal
                isOpen={true}
                // toggle={() => { tog_center() }}
                size="modal-fullscreen-xxl-down"
            >
                <div>
                    <Row className='px-md-4 pt-3 '>
                        <Label>Category</Label>
                        <p>jfhgv</p>
                    </Row>
                    <Row className='px-4'>
                        {fun1(data)}
                    </Row>
                </div>

            </Modal>

        </div>


    )
}

