import React, { useEffect, useRef, useState, } from "react";
import Breadcrumb from "../../../components/Common/Breadcrumb";
import {
    Card,
    CardBody,
    CardHeader,
    Col,
    Container,
    FormGroup,
    Label,
    Row,
    Table,
    Button,
} from "reactstrap";
import { AvField, AvForm, } from "availity-reactstrap-validation";
import Select from "react-select";
import { MetaTags } from "react-meta-tags";
import { BreadcrumbShow } from "../../../store/actions";
import { useDispatch, useSelector } from "react-redux";
import { Tbody,Thead} from "react-super-responsive-table";
import { AlertState } from "../../../store/actions";
import { CommonGetRoleAccessFunction } from "../../../components/Common/CommonGetRoleAccessFunction";
import { getMethodForVehicle, getMethod_ForDriverList_ForDropDown, getMethod_ForVehicleTypes_ForDropDown, PostMethodForVehicle, PostMethod_ForVehicleAPISuccess } from "../../../store/Administrator/VehicleRedux/action";
import { getDivisionTypesID } from "../../../store/Administrator/PartyRedux/action";
// import { actionChannel } from "redux-saga/effects";



const VehicleMaster = (props) => {
    const formRef = useRef(null);
    const [divisionTypeData, setDivisionTypeData] = useState([]);
    const [divisionType_dropdown_Select, setDivisionType_dropdown_Select] = useState("");
    const [DriverList_dropdown_Select,setDriverList_dropdown_Select]= useState("");
    const [VehicleType_dropdown_Select,setVehicleType_dropdown_Select]=useState("");
    const [pageMode, setPageMode] = useState("");
    const dispatch = useDispatch();
    const [userPageAccessState, setUserPageAccessState] = useState(123);
    const [EditData, setEditData] = useState([]);
  
    //Access redux store Data /  'save_ModuleSuccess' action data
    const { PostAPIResponse, DivisionType, DriverList, VehicleType } = useSelector((state) => ({
        //  PostAPIResponse: state.VehicleMasterReducer.PostDataMessage,
         DivisionType: state.PartyMasterReducer.partyList,
         DriverList : state.VehicleReducer,
        //  VehicleTypes : state.VehicleMasterReducer.VehicleTypes,
    }));

console.log("DriverList",DriverList)
    //*** "isEditdata get all data from ModuleID for Binding  Form controls
    let editDataGatingFromList = props.state;

    useEffect(() => {
        dispatch(getMethodForVehicle());
        dispatch(getMethod_ForDriverList_ForDropDown());
        dispatch(getMethod_ForVehicleTypes_ForDropDown());
        dispatch(getDivisionTypesID());
        
    }, [dispatch]);

    useEffect(() => {
        if ((PostAPIResponse.Status === true) && (PostAPIResponse.StatusCode === 200)) {
            // setSubCategory_dropdown_Select('')
            dispatch(PostMethod_ForVehicleAPISuccess({ Status: false }))
            formRef.current.reset();
            if (pageMode === "other") {
                dispatch(AlertState({
                    Type: 1,
                    Status: true,
                    Message: PostAPIResponse.Message,
                }))
            }
            else {
                dispatch(AlertState({
                    Type: 1,
                    Status: true,
                    Message: PostAPIResponse.Message,
                    // RedirectPath: '/SubCategoryList',
                }))
            }
        }
        else if (PostAPIResponse.Status === true) {
            dispatch(PostMethod_ForVehicleAPISuccess({ Status: false }))
            dispatch(AlertState({
                Type: 4,
                Status: true,
                Message: JSON.stringify(postMessage.Message),
                RedirectPath: false,
                AfterResponseAction: false
            }));
        }
    }, [PostAPIResponse])

    
    const DivisionType_DropdownOptions = DivisionType.map((data) => ({
        value: data.id,
        label: data.Name
    }));

    function DivisionType_DropDown_handller(e) {
        setDivisionType_dropdown_Select(e)
    }

    const DriverList_DropdownOptions = DriverList.map((data) => ({
       value: data.id,
       label: data.Name
     }));

     function DriverList_DropDown_handller(e) {
        setDriverList_dropdown_Select(e)
    }

    const VehicleType_DropdownOptions = VehicleType.map((data) => ({
        value: data.id,
        label: data.Name
      }));
 
    function VehicleType_DropDown_handller(e) {
        setVehicleType_dropdown_Select(e)
    }

    const FormSubmitButton_Handler = (event, values) => {
        const jsonBody = JSON.stringify({
            Name: values.Name,
            
        });
          dispatch(PostMethodForVehicle(jsonBody));  
    };

 /// Role Table Validation
 function AddDivisionHandler() {
    const find = divisionTypeData.find((element) => {
        return element.value === divisionType_dropdown_Select.value
    });

    if (divisionType_dropdown_Select.length <= 0) {
        dispatch(AlertState({
            Type: 3, Status: true,
            Message: "Select One Role",
        }));
    }
    else if (find === undefined) {
        setDivisionTypeData([...divisionTypeData, divisionType_dropdown_Select]);
    }
    else {
        dispatch(AlertState({
            Type: 4, Status: true,
            Message: "DivisionType already Exists ",
        }));
    }
}



 // For Delete Button in table
 function UserRoles_DeleteButton_Handller(tableValue) {
    setDivisionTypeData(divisionTypeData.filter(
        (item) => !(item.value === tableValue)
    )
    )
}

    // IsEditMode_Css is use of module Edit_mode (reduce page-content marging)
