        // setBaseUnit_dropdown_Select2(e)
    }

    function DivisionTab_AddRow_Handle() {
        const find = divisionTableData.find((element) => {
            return element.value === division_dropdown_Select.value
        });

        if (division_dropdown_Select.length <= 0) {
            dispatch(AlertState({
                Type: 3, Status: true,
