export default function TreeInput({ data = [] }) {
    // const [treeValue, setTreeValue] = useState("party");

 function fun1(data){
    return (
        <Row className='px-md-4 mt-1 '>
                        <Col md={1}>...</Col>
                        <Col md={10}>
                            <div className=" row form-group form-group-lg">
                                {/* <Label className='col col-4'>text</Label> */}
                                    <Input type="text" ></Input>

                            </div>
                        </Col>
                        <Col md={1}>...</Col>
{data.childern?fun2(data.childern):null}
                    </Row>
    )
 }
 
