    return(
        <Row className='px-md-4  mt-1'>
                        <Col md={1}>...</Col>
                        <Col md={11}>
                            <div className=" row form-group form-group-lg">
                                {/* <Label className='col col-4'>text</Label> */}
                                    <Input type="text" ></Input>

                            </div>
                        </Col>
                        
                        {data.childern?fun1(data.childern):null}

                    </Row>
    )
    
}



    return (

        <div>
            <Modal
                isOpen={true}
                // toggle={() => { tog_center() }}
                size="modal-fullscreen-xxl-down"
            >
                <div>
                    <Row className='px-md-4 pt-3 '>
                        <Label>Category</Label>
                        <p>jfhgv</p>
                    </Row>
{ data.map(tree=>fun1(tree))}
                    
                </div>

            </Modal>

        </div>


    )
}

