import React, { useState } from 'react'
import FeatherIcon from "feather-icons-react";
import { Col, DropdownMenu, DropdownToggle, Input, Label, Modal, Row, UncontrolledDropdown } from 'reactstrap';

export default function TreeInput({ data = [] }) {
    // const [treeValue, setTreeValue] = useState("party");

    function fun1(data1) {
        return (
            <>
                <ul>
                    {
                        data1.map(tree => fun2(tree))
                    }
                </ul>

                {/* <Row className='px-md-4 mt-1 '>
               <Col md={1}>...</Col>
              <Col md={10}>
                   <div className=" row form-group form-group-lg">
                       <Input type="text" ></Input>

                   </div>
              </Col>
              <Col md={1}>...</Col>
                {data.childern ? fun2(data.childern) : null}
            </Row> */}
            </>
        )
    }

    function fun2(data2) {
        return (
            <li>
            <Row className='px-md-4  mt-1'>
                <Col md={1}>...</Col>
                <Col md={11}>
                    <div className=" row form-group form-group-lg">
                        {/* <Label className='col col-4'>text</Label> */}
                        <Input type="text" ></Input>

                    </div>
                </Col>

                {data2.childern ? fun1(data2.childern) : null}

            </Row>
            </li>
        )

    }



    return (

        <div>
            <Modal
                isOpen={true}
                // toggle={() => { tog_center() }}
                size="modal-fullscreen-xxl-down"
            >
                <div>
                    <Row className='px-md-4 pt-3 '>
                        <Label>Category</Label>
                        <p>jfhgv</p>
                    </Row>
                    {fun1(data)}
                </div>

            </Modal>

        </div>


    )
}

