export const basicAmount = i => {
    debugger
    return (i.inpRate * i.inpQty)
}

export const GstAmount = (i) => {
    debugger
    return basicAmount(i) * (i.GST / 100)
}

export const totalAmount = (i) => {
    debugger
    return GstAmount(i) + basicAmount(i)
}
