import React, { useState } from 'react'
import FeatherIcon from "feather-icons-react";
import { Col, DropdownMenu, DropdownToggle, Input, Label, Modal, Row, UncontrolledDropdown } from 'reactstrap';

export default function TreeInput({ data = [] }) {
    // const [treeValue, setTreeValue] = useState("party");

    function fun1(data1) {
        return (
         
                <ul>
                    {
                        data1.map(tree => fun2(tree))
                    }
                </ul>

      
           
        )
    }

    function fun2(data2) {
        return (
            <li >
            <Row style={{paddingLeft:"20px"}}>
                <div className='col col-11'>
                        <Input type="text"  value={data2.Name}></Input>
                    {/*  */}
                </div>
                <div className='col col-1'>..</div>

                {data2.childern ? fun1(data2.childern) : null}

            </Row>
            </li>
        )

    }



    return (

        <div>
            <Modal
                isOpen={true}
                // toggle={() => { tog_center() }}
                size="modal-fullscreen-xxl-down"
            >
                <div>
                    <Row className='px-md-4 pt-3 '>
                        <Label>Category</Label>
                        <p>jfhgv</p>
                    </Row>
                    <Row className='px-4'>
                    {fun1(data)}
                    </Row>
                </div>

            </Modal>

        </div>


    )
}

