import Flatpickr from "react-flatpickr";
import Select from "react-select";
import { useDispatch, useSelector } from "react-redux";
import "flatpickr/dist/themes/material_blue.css"

import React, { useEffect, useState, useRef } from "react";
import { MetaTags } from "react-meta-tags";

import ToolkitProvider from "react-bootstrap-table2-toolkit";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, { PaginationListStandalone, PaginationProvider } from "react-bootstrap-table2-paginator";
import { useHistory } from "react-router-dom";
import { editOrderIdSuccess, getSupplier, goButton, goButtonSuccess, postOrder, postOrderSuccess } from "../../../store/Purchase/OrderPageRedux/actions";
import { mySearchProps } from "../../../components/Common/CmponentRelatedCommonFile/SearchBox/MySearch";
import { AlertState } from "../../../store/actions";
import { basicAmount, GstAmount, handleKeyDown, totalAmount } from "./OrderPageCalulation";
import '../../Order/div.css'
import SaveButton1 from "../../../components/CommonSaveButton";

let description = ""
let editItemValue = {}
const Order = (props) => {

    const dispatch = useDispatch();
    const history = useHistory();

    const [modalCss, setModalCss] = useState(false);
    const [pageMode, setPageMode] = useState("save");
    const [userPageAccessState, setUserPageAccessState] = useState("");

    //Access redux store Data /  'save_ModuleSuccess' action data

    const [effectiveDate, setEffectiveDate] = useState("");
    const [supplier_select, setSupplier_select] = useState('');
    const [orderAmount, setOrderAmount1] = useState("");

    useEffect(() => {
        dispatch(getSupplier())
    }, [])

    const {
        items,
        postMsg,
        supplier,
        userAccess,
        pageField
    } = useSelector((state) => ({
        items: state.OrderReducer.orderItem,
        supplier: state.OrderReducer.supplier,
        postMsg: state.OrderReducer.postMsg,
        userAccess: state.Login.RoleAccessUpdateData,
        pageField: state.CommonPageFieldReducer.pageFieldList
    }));



    // userAccess useEffect
    useEffect(() => {
        let userAcc = null;
        let locationPath = location.pathname;

        if (hasShowModal) { locationPath = props.masterPath; };

        userAcc = userAccess.find((inx) => {
            return (`/${inx.ActualPagePath}` === locationPath)
        })

        if (userAcc) {
            setUserPageAccessState(userAcc)
        };
    }, [userAccess])

    const location = { ...history.location }
    const hasShowloction = location.hasOwnProperty("editValue")
    const hasShowModal = props.hasOwnProperty("editValue")

    useEffect(() => {

        // if (!(userPageAccessState === '')) { document.getElementById("txtName").focus(); }
        if ((hasShowloction || hasShowModal)) {
            editItemValue = {}
            let hasEditVal = null
            if (hasShowloction) {
                setPageMode(location.pageMode)
                hasEditVal = location.editValue
            }
            else if (hasShowModal) {
                hasEditVal = props.editValue
                setPageMode(props.pageMode)
                setModalCss(true)
            } else {
                editItemValue = {}
            }

            if (hasEditVal) {
                const jsonBody = JSON.stringify({
                    Supplier: hasEditVal.Supplier,
                    EffectiveDate: hasEditVal.OrderDate
                }
                );
                editItemValue = hasEditVal

                dispatch(goButton(jsonBody))
                setSupplier_select({ label: hasEditVal.SupplierName, value: hasEditVal.Supplier })
                setEffectiveDate(hasEditVal.OrderDate)
                description = hasEditVal.Description
                setOrderAmount1(hasEditVal.OrderAmount)

            }
            dispatch(editOrderIdSuccess({ Status: false }))
        }

    }, [])

    useEffect(() => {
        
        if (pageMode === "edit") {
            const result = Array.isArray(editItemValue.OrderItem);
            if (result) {
                const aaa = []
                let obj = {}
                items.forEach((element, k) => {
                    editItemValue.OrderItem.forEach(ele => {
                        obj = element
                        if (element.id === ele.Item) {

                            // document.getElementById(`inpRatey${k}`).value = ele.Rate
                            //     document.getElementById(`inpQty${k}`).value = ele.Quantity
                            obj["inpRate"] = ele.Rate
                            obj["totalAmount"] = ele.Quantity
                            obj["inpQty"] = ele.Amount
                        }
                        aaa.push(obj);
                        obj = {}

                    });

                });
                debugger
                editItemValue = {}
                dispatch(goButtonSuccess(aaa))

            }
        }
        else {
            editItemValue = {}
        }


    }, [items])

    useEffect(() => {
        if ((postMsg.Status === true) && (postMsg.StatusCode === 200)) {
            dispatch(postOrderSuccess({ Status: false }))
            dispatch(goButtonSuccess([]))
            dispatch(AlertState({
                Type: 1,
                Status: true,
                Message: postMsg.Message,
                RedirectPath: false,
            }))

        } else if (postMsg.Status === true) {
            dispatch(postOrderSuccess({ Status: false }))
            dispatch(AlertState({
                Type: 4,
                Status: true,
                Message: "error Message",
                RedirectPath: false,
                AfterResponseAction: false
            }));
        }
    }, [postMsg])

    function valChange(val, row, type) { 
        if (type === "qty") {
            row["inpQty"] = val;
        }
        else {
            row["inpRate"] = val
        }
        row["totalAmount"] = totalAmount(row)
        debugger
        let sum = 0
        items.forEach(ind => {
            sum = sum + parseFloat(ind.totalAmount)
        });
        setOrderAmount1(sum.toFixed(2))
    }

    const supplierOptions = supplier.map((i) => ({
        value: i.id,
        label: i.Supplier,
    }));

    const pagesListColumns = [
        {
            text: "Item Name",
            dataField: "Name",
            sort: true,
        },
        {
            text: "Rate",
            dataField: "Rate",
            sort: true,
            formatter: (value, row, k) => {
                if (row.inpRate === undefined) { row["inpRate"] = 0 }
                if (row.totalAmount === undefined) { row["totalAmount"] = 0 }
                debugger
                return (
                    <span className="text-right" >
                        <Input
                            type="text"
                            id={`inpRatey${k}`}
                            value={row.inpRate}
                            onChange={e => {
                                row["inpRate"] = e.target.value;
                                const qty = document.getElementById(`inpQty${k}`)
                                const val = e.target.value
                                if (val > 0) {

                                    valChange(val, row, "rate")
                                    qty.disabled = false
                                } else {
                                    qty.value = ''
                                    row["inpQty"] = 0;
                                    valChange(0, row, "rate")
                                    qty.disabled = true
                                }
                            }}
                            onKeyDown={(e) => handleKeyDown(e, items)}
                        />
                    </span>
                )
            },

            headerStyle: (colum, colIndex) => {
                return { width: '140px', textAlign: 'center' };
            }
        },
        {
            text: "GST %",
            dataField: "GST",
            sort: true,
            formatter: (value, row) => (

                <span >
                    {value}
                </span>

            ),
            headerStyle: (colum, colIndex) => {
                return { width: '130px', textAlign: 'center', text: "left" };
            }

        },
        {
            text: "Quntity",
            dataField: "",
            sort: true,
            formatter: (value, row, k) => (

                <span >
                    <Input type="text"
                        id={`inpQty${k}`}
                        defaultValue={row.inpQty}
                        disabled={(row.inpRate === 0) ? true : false}
                        onChange={(e) => {
                            valChange(e.target.value, row, "qty")
                        }}
                        autoComplete="off"
                        onKeyDown={(e) => handleKeyDown(e, items)} />
                </span>

            ),
            headerStyle: (colum, colIndex) => {
                return { width: '140px', textAlign: 'center' };
            }


        },
        {
            text: "UOM",
            dataField: "",
            sort: true,
            formatter: (value, row, key) => {

                if (row.UOMLabel === undefined) {
                    row["UOM"] = row.UnitDetails[0].UnitID
                    row["UOMLabel"] = row.UnitDetails[0].UnitName
                    row["inpBaseUnitQty"] = row.UnitDetails[0].BaseUnitQuantity
                }
                return (
                    <Select
                        classNamePrefix="select2-selection"
                        id={"ddlUnit"}
                        defaultValue={{ value: row.UOM, label: row.UOMLabel }}
                        // value={{value:row.UOM,label:row.UOMLabel}}
                        options={
                            row.UnitDetails.map(i => ({
                                label: i.UnitName,
                                value: i.UnitID,
                                baseUnitQty: i.BaseUnitQuantity
                            }))
                        }
                        onChange={e => {
                            row["UOM"] = e.value;
                            row["UOMLabel"] = e.label
                            row["inpBaseUnitQty"] = e.baseUnitQty
                        }}
                    >
                    </Select >
                )
            },
            headerStyle: (colum, colIndex) => {
                return { width: '150px', textAlign: 'center' };
            }

        },

    ];

    const defaultSorted = [
        {
            dataField: "PriceList", // if dataField is not match to any column you defined, it will be ignored.
            order: "asc", // desc or asc
        },
    ];

    const pageOptions = {
        sizePerPage: (items.length + 2),
        totalSize: 0,
        custom: true,
    };

    const EffectiveDateHandler = (e, date) => {
        setEffectiveDate(date)
    }

    const GoButton_Handler = () => {
        let supplier = supplier_select.value

        if (!supplier > 0) {
            alert("Please Select Customer")
            return
        }

        if (items.length > 0) {
            if (window.confirm("Refresh Order Item...!")) {
                dispatch(goButtonSuccess([]))
            } else {
                return
            }
        }

        // let division = 0
        // try {
        //     division = JSON.parse(localStorage.getItem("roleId")).Party_id
        // } catch (e) {
        //     alert(e)
        // }
        const jsonBody = JSON.stringify({
            Supplier: supplier,
            EffectiveDate: effectiveDate
        }
        );

        dispatch(goButton(jsonBody))
        console.log("jsonBody", jsonBody)
    };

    const saveHandeller = () => {
        let division = 0
        let date
        let supplier = supplier_select.value

        try {
            division = JSON.parse(localStorage.getItem("roleId")).Party_id
            date = document.getElementById("EffectiveDateid").value;
        } catch (e) {
            alert(e)
            return
        }

        const itemArr = []
        items.forEach(i => {
            if ((i.inpQty > 0)) {
                const arr = {
                    Item: i.id,
                    Quantity: i.inpQty,
                    MRP: i.MRP,
                    Rate: i.inpRate,
                    Unit: i.UOM,
                    BaseUnitQuantity: i.inpBaseUnitQty,
                    GST: i.Gstid,
                    Margin: "",
                    BasicAmount: basicAmount(i).toFixed(2),
                    GSTAmount: GstAmount(i).toFixed(2),
                    CGST: i.GST,
                    SGST: (GstAmount(i) / 2).toFixed(2),
                    IGST: (GstAmount(i) / 2).toFixed(2),
                    CGSTPercentage: (i.GST / 2),
                    SGSTPercentage: (i.GST / 2),
                    IGSTPercentage: (i.GST),
                    Amount: i.totalAmount,
                }
                itemArr.push(arr)
            };
        })
        debugger
        if (itemArr.length === 0) {
            alert("Please Enter one Item Quantity")
            return
        }

        const jsonBody = JSON.stringify({
            OrderDate: date,
            Customer: division,
            Supplier: supplier,
            OrderAmount: orderAmount,
            Description: description,
            CreatedBy: 1,
            UpdatedBy: 1,
            OrderItem: itemArr
        });
        dispatch(postOrder(jsonBody))
        console.log(jsonBody)


    }

    if (!(userPageAccessState === "")) {
        return (
            <React.Fragment>
                <MetaTags>
                    <title>{userPageAccessState.PageHeading}| FoodERP-React FrontEnd</title>
                </MetaTags>
                <div className="page-content">
                    {/* <Breadcrumb
                    title={"Count :"}
                    breadcrumbItem={userPageAccessState.PageHeading ? userPageAccessState.PageHeading : "Order"}
                    IsButtonVissible={(userPageAccessState.RoleAccess_IsSave) ? true : false}
                    breadcrumbCount={`Product Count: ${"searchCount"}`}
                    // SearchProps={searchProps}
                    // IsSearchVissible={true}
                    RedirctPath={"/#"}
                    isExcelButtonVisible={true}
                    ExcelData={items}
                /> */}



                    <div className="d-flex  justify-content-between">
                        <div >
                            <label className="font-size-18 form-label text-black pl-2" >
                                {"Order"}
                            </label>
                        </div>
                        <div >
                            <label className="font-size-16 form-label  bd-highlight text-primary rounded pr-2" >
                                Order Amount : &nbsp;
                                <kbd className="bg-light text-danger font-size-22">{orderAmount}</kbd>
                            </label>
                        </div>
                    </div>
                    <div className="px-2">
                        <Row className="mb-1 border border-black text-black mt-2 "
                            style={{ backgroundColor: "#dddddd" }} >

                            <Col md="3" className="">
                                <FormGroup className="mb- row mt-3 " >
                                    <Label className="col-sm-5 p-2"
                                        style={{ width: "100px" }}>Order Date</Label>
                                    <Col md="7">
                                        <Flatpickr
                                            id="EffectiveDateid"
                                            name="effectiveDate"
                                            value={effectiveDate}
                                            className="form-control d-block p-2 bg-white text-dark"
                                            placeholder="Select..."
                                            options={{
                                                altInput: true,
                                                altFormat: "F j, Y",
                                                dateFormat: "Y-m-d",
                                                minDate: pageMode === "edit" ? effectiveDate : "today",
                                                maxDate: pageMode === "edit" ? effectiveDate : "",
                                                defaultDate: pageMode === "edit" ? "" : "today"
                                            }}
                                            onChange={EffectiveDateHandler}
                                        />
                                    </Col>
                                </FormGroup>
                            </Col>

                            <Col md="3">
                                <FormGroup className="mb-2 row mt-3 " >
                                    <Label className="col-md-4 p-2"
                                        style={{ width: "130px" }}>Supplier Name</Label>
                                    <Col md="7">
                                        <Select
                                            value={supplier_select}
                                            classNamePrefix="select2-Customer"
                                            isDisabled={pageMode === "edit" ? true : false}
                                            options={supplierOptions}
                                            onChange={(e) => { setSupplier_select(e) }}
                                        />
                                    </Col>
                                </FormGroup>
                            </Col >

                            <Col md="1" className="mt-3 ">
                                <Button type="button" color="btn btn-outline-success border-2 font-size-12 "
                                    onClick={GoButton_Handler}
                                >Go</Button>
                            </Col>
                            <Col>
                                <FormGroup className="mb-2 d-flex  justify-content-end mt-3 " >
                                    <Label className=" p-2 ml-n4 "
                                        style={{ width: "100px" }}>Description</Label>
                                    <div>
                                        <Input
                                            defaultValue={description}
                                            placeholder="Enter Description"
                                            onChange={(e) => { description = e.target.value }}
                                        />
                                    </div>
                                </FormGroup>
                            </Col>
                        </Row>
                    </div>

                    <PaginationProvider pagination={paginationFactory(pageOptions)}>
                        {({ paginationProps, paginationTableProps }) => (
                            <ToolkitProvider
                                keyField="id"
                                defaultSorted={defaultSorted}
                                data={items}
                                columns={pagesListColumns}
                                search
                            >
                                {(toolkitProps,) => (
                                    <React.Fragment>

                                        {/* <Breadcrumb
                                                    title={"Count :"}
                                                    breadcrumbItem={userPageAccessState.PageHeading}
                                                    IsButtonVissible={(userPageAccessState.RoleAccess_IsSave) ? true : false}
                                                    SearchProps={toolkitProps.searchProps}
                                                    breadcrumbCount={`Product Count: ${items.length}`}
                                                    IsSearchVissible={true}
                                                    RedirctPath={"masterPath"}
                                                    isExcelButtonVisible={true}
                                                    ExcelData={items}
                                                /> */}

                                        <Row>

                                            <Col xl="12">
                                                <div className="table table-unRresponsive">
                                                    <BootstrapTable
                                                        keyField={"id"}
                                                        responsive
                                                        bordered={false}
                                                        striped={false}
                                                        classes={"table  table-bordered table-hover"}

                                                        noDataIndication={
                                                            <div className="text-danger text-center ">
                                                                Items Not available
                                                            </div>
                                                        }
                                                        {...toolkitProps.baseProps}
                                                        {...paginationTableProps}
                                                    />

                                                    {mySearchProps(toolkitProps.searchProps)}
                                                </div>
                                            </Col>
                                        </Row>
                                        <Row className="align-items-md-center mt-30">
                                            <Col className="pagination pagination-rounded justify-content-end mb-2">
                                                <PaginationListStandalone {...paginationProps} />
                                            </Col>
                                        </Row>
                                    </React.Fragment>
                                )}
                            </ToolkitProvider>
                        )}

                    </PaginationProvider>

                    {(items.length > 0) ? <div className="row save1" style={{ paddingBottom: 'center' }}>
                        <SaveButton1 pageMode={pageMode} userPageAccessState={userPageAccessState} module={"Order"}
                            onClick={saveHandeller}
                        />

                    </div>
                        : <div className="row save1"></div>}
                </div>
                {/* </div> */}

            </React.Fragment>
        )
    } else {
        return null
    }

}
export default Order
