        else if (type === 'Margin') {
            newSelectValue = {
                PriceList: found.PriceList,
                Margin: event.target.value,
            }
        }

        let newTabArr = marginTabTable.map((index, k) => {
            return (k === key) ? newSelectValue : index
        })
        setMarginTabTable(newTabArr)
    }


