import {
  Button,
  Card,
  CardBody,
  Col,
  FormGroup,
  Input,
  Label,
  Row,
  Table,
} from "reactstrap";
import Flatpickr from "react-flatpickr";
import Select from "react-select";
import { useDispatch, useSelector } from "react-redux";


import React, { useEffect, useState } from "react";
import { Tbody, Thead } from "react-super-responsive-table";

import cellEditFactory, { Type } from 'react-bootstrap-table2-editor';
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, { PaginationListStandalone, PaginationProvider } from "react-bootstrap-table2-paginator";

function MRPTable(props) {



  const { Party, Division } = useSelector((state) => ({
    Division: state.ItemMastersReducer.Division,
    Party: state.ItemMastersReducer.Party,
  }));


  const Party_DropdownOptions = Party.map((data) => ({
    value: data.id,
    label: data.Name,
  }));

  const Division_DropdownOptions = Division.map((data) => ({
    value: data.id,
    label: data.Name,
  }));

  const ondeleteHandeler = (ele) => {

    var fil = props.tableData.filter((i) => {
      return !(i.id === ele.id);
    });
    props.func(fil);

  };
  const partyOnchange = (e, row) => {
    row["PartyName"] = e.label
    row["Party"] = e.value;
    row["ispty"] = true

  }


  const divisionOnchange = (e, row) => {
    row["DivisionName"] = e.label
    row["Division"] = e.value;
    row["ispty"] = true

  }

  const effectiveDateOnchange = (e, row) => {
    row["PartyName"] = e.label
    row["Party"] = e.value;
    row["ispty"] = true

  }
  const options = [
    { value: 'active', label: 'Active' },
    { value: 'inactive', label: 'In Active' },
    { value: 'deleted', label: 'Delete' },
  ];


  const pagesListColumns = [
    {
      text: "DivisionName",
      dataField: "DivisionName",
      sort: true,
      formatter: (value, row) => (
        <span className={`${row.ispty ? "text-warning" : null}`}>
          {value}
        </span>
      ),
      editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (
        <>
          <Select
            value={{
              label: row.DivisionName,
              value: row.Division
            }}
            isSearchable={false}
            className="react-dropdown"
            // onChange={(v, e) => onChangeSelect({ e, v, state, setState })}
            classNamePrefix="dropdown"
            options={Party_DropdownOptions}
            name="Party"
            styles={{
              control: base => ({
                ...base,
                // border: isError.Address.length > 0 ? '1px solid red' : '',

              })
            }}
            onChange={(e) => divisionOnchange(e, row)}
          />
        </>


      )
    },
    {
      text: "PartyName",
      dataField: "PartyName",
      sort: true,
      formatter: (value, row) => (
        <div style={{ backgroundColor: "#FFFAFA" }}>
          <span className={`${row.ispty ? "text-info" : null}`}>
            {value}
          </span>
        </div>
      ),

      editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (
        <>
          <Select
            value={{
              label: row.PartyName,
              value: row.Party
            }}
            isSearchable={false}
            className="react-dropdown"
            // onChange={(v, e) => onChangeSelect({ e, v, state, setState })}
            classNamePrefix="dropdown"
            options={Party_DropdownOptions}
            name="Party"
            styles={{
              control: base => ({
                ...base,
                // border: isError.Address.length > 0 ? '1px solid red' : '',

              })
            }}
            onChange={(e) => partyOnchange(e, row)}
          />
        </>
      )
    },


    {
      text: "EffectiveDate",
      dataField: "EffectiveDate",
      sort: true,
      formatter: (value, row) => (
        <div style={{ backgroundColor: "#FFFAFA" }}>
          <span className={`${row.ispty ? "text-info" : null}`}>
            {value}
          </span>
        </div>
      ),
      editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (

        <>

          <Flatpickr
            name="DOB"
            className="form-control d-block p-2 bg-white text-dark"
            placeholder="YYYY-MM-DD"
            autoComplete="0,''"
            options={{
              altInput: true,
              altFormat: "F j, Y",
              dateFormat: "Y-m-d",
              minDate: new Date().fp_incr("n"),
              maxDate: new Date().fp_incr(0) // 14 days from now"0,''"
            }}
            // onChange={(y, v, e) => { onChangeDate({ e, v, state, setState }) }}
            onChange={(y, e) => effectiveDateOnchange(e, row)}
          />
        </>


      )
    },

    {
      text: "MRP",
      dataField: "MRP",
      sort: true,
      formatter: (value, row) => (
        <div style={{ backgroundColor: "#FFFAFA" }}>
          <span className={`${row.ispty ? "text-info" : null}`}>
            {value}
          </span>
        </div>
      ),
      editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (

        <Input type="text" value={value} onChange={(e) => MRP_onChange(e, row)} />
      )
    },
    {
      text: "Action",
      dataField: "",
      sort: true,
      formatter: (value, row) => (
        <div style={{ backgroundColor: "#FFFAFA" }}>



        </div>
      ),
      editorRenderer: (editorProps, value, row, column, rowIndex, columnIndex) => (
        <Button
          className="badge badge-soft-danger font-size-12 btn btn-danger 
          waves-effect waves-light w-xxs border border-light"
          data-mdb-toggle="tooltip"
          data-mdb-placement="top"
          title="Delete Party Type"
          onClick={(e) => ondeleteHandeler(row)}
        >
          <i className="mdi mdi-delete font-size-18"></i>
        </Button>

      )

    },



  ];
  const defaultSorted = [
    {
      dataField: "Name", // if dataField is not match to any column you defined, it will be ignored.
      order: "asc", // desc or asc
    },
  ];

  const pageOptions = {
    sizePerPage: 10,
    totalSize: props.tableData.length,
    custom: true,
  };

  return (
    <PaginationProvider pagination={paginationFactory(pageOptions)}>
      {({ paginationProps, paginationTableProps }) => (
        <ToolkitProvider
          keyField="id"
          defaultSorted={defaultSorted}
          data={props.tableData}
          columns={pagesListColumns}
          search
        >
          {(toolkitProps) => (
            <React.Fragment>

              <Row>
                <Col xl="12">
                  <div className="table">
                    <BootstrapTable
                      keyField={"id"}
                      responsive
                      bordered={false}
                      striped={false}
                      cellEdit={cellEditFactory({ mode: 'click', blurToSave: true })}
                      classes={"table  table-bordered"}
                      {...toolkitProps.baseProps}
                      {...paginationTableProps}
                    />
                  </div>
                </Col>
              </Row>
              <Row className="align-items-md-center mt-30">
                <Col className="pagination pagination-rounded justify-content-end mb-2">
                  <PaginationListStandalone {...paginationProps} />
                </Col>
              </Row>
            </React.Fragment>
          )}
        </ToolkitProvider>
      )}

    </PaginationProvider>
  )
}
export default MRPTable
