import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import {
  deleteDriverTypeIDSuccess,
  updateDriverTypeIDSuccess,
  getMethodForDriverList,
  editDriverTypeId,
  delete_DriverType_ID,
  PostMethod_ForDriverMasterSuccess,
} from "../../../store/Administrator/DriverRedux/action";
import { listPageCommonButtonFunction }
  from "../../../components/Common/CmponentRelatedCommonFile/listPageCommonButtons";
import CommonListPage from "../../../components/Common/CmponentRelatedCommonFile/commonListPage";
import DriverMaster from "../../../pages/Adminisrator/DriverPage/DriverMaster";
import { commonPageField } from "../../../store/actions";

const DriverList = (props) => {

  const dispatch = useDispatch();
  const history = useHistory()

  const [userPageAccState, setUserPageAccState] = useState('');

  const redux = useSelector(
    (state) => ({
      tableListData: state.DriverReducer.DriverList,
      editData: state.DriverReducer.editData,
      updateResp: state.DriverReducer.updateMessage,
      deleteResp: state.DriverReducer.deleteMessage,
      postResp: state.DriverReducer.PostDataMessage,
      pageFiled: state.CommonPageFieldReducer.pageField
    })
  );
  const { roleAccessSingleArray } = useSelector((state) => ({
    roleAccessSingleArray: state.Login.RoleAccessUpdateData,
  })
  );


  useEffect(() => {
    const locationPath = history.location.pathname
    let isUserAcc = roleAccessSingleArray.find((inx) => {
      return (`/${inx.ActualPagePath}` === locationPath)
    })
    if (!(isUserAcc === undefined)) {
      setUserPageAccState(isUserAcc)
    }
  }, [roleAccessSingleArray])


  useEffect(() => {
    dispatch(commonPageField(89))
  }, []);

  var column = []

  debugger
  redux.pageFiled.forEach(i,k => {
    if (i.ShowInListPage) {
      column.push({
        text: i.FieldLabel,
        dataField: i.ControlID,
        sort: i.DefaultSort,
      })
    }
    if((redux.pageFiled.lentgh-1===k)){
      listPageCommonButtonFunction({
        dispatchHook: dispatch,
        ButtonMsgLable: "DriverType",
        deleteName: "Name",
        userPageAccessState: userPageAccState,
        editActionFun: editDriverTypeId,
        deleteActionFun: delete_DriverType_ID
      })
    }
  });


  const tabelColumns = [
    {
      text: "Name",
      dataField: "Name",
      sort: true,
    },

    {
      text: "Date Of Birth",
      dataField: "DOB",
      sort: true,
    },
    {
      text: "Address",
      dataField: "Address",
      sort: true,
    },
    {
      text: "UID",
      dataField: "UID",
      sort: true,
    },

    // For Edit, Delete ,and View Button Common Code function
    listPageCommonButtonFunction({
      dispatchHook: dispatch,
      ButtonMsgLable: "DriverType",
      deleteName: "Name",
      userPageAccessState: userPageAccState,
      editActionFun: editDriverTypeId,
      deleteActionFun: delete_DriverType_ID
    })
  ];

  const redxActions = {
    getList: getMethodForDriverList,
    updateSuccess: updateDriverTypeIDSuccess,
    deleteSuccess: deleteDriverTypeIDSuccess,
    postSucess: PostMethod_ForDriverMasterSuccess
  }



  return (
    <React.Fragment>
      <CommonListPage
        selector={redux}
        tabelColumns={tabelColumns}
        userPageAccState={userPageAccState}
        redxActions={redxActions}
        sortName={"Name"}
        masterPath={"/DriverMaster"}
        MasterComp={DriverMaster}
      ></CommonListPage>
    </React.Fragment>
  )
}

export default DriverList;
