import React, { useEffect, useState } from "react";
import Breadcrumbs from "../../../components/Common/Breadcrumb";
import { Col, Modal, Row } from "reactstrap";
import paginationFactory, {
  PaginationListStandalone,
  PaginationProvider,
} from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import BootstrapTable from "react-bootstrap-table-next";
import { useSelector, useDispatch } from "react-redux";
import "../../../assets/scss/CustomeTable/datatables.scss";
import DriverMaster from "./DriverMaster";
import { MetaTags } from "react-meta-tags";
import { useHistory } from "react-router-dom";
import {
  deleteDriverTypeIDSuccess,
  updateDriverTypeIDSuccess,
  getMethodForDriverList,
  editDriverTypeId,
  delete_DriverType_ID,
  PostMethod_ForDriverMasterSuccess,
} from "../../../store/Administrator/DriverRedux/action";
import { AlertState } from "../../../store/actions";
import { listPageCommonButtonFunction }
  from "../../../components/Common/CmponentRelatedCommonFile/listPageCommonButtons";
import CommonListPage from "../../../components/Common/CmponentRelatedCommonFile/commonListPage";

const DriverList = (props) => {

  const dispatch = useDispatch();
  const history = useHistory()

  const [userPageAccState, setUserPageAccState] = useState('');


  const  redux= useSelector(
    (state) => ({
      TableListData: state.DriverReducer.DriverList,
      editData: state.DriverReducer.editData,
      updateMessage: state.DriverReducer.updateMessage,
      deleteMessage: state.DriverReducer.deleteMessage,
      RoleAccessModifiedinSingleArray: state.Login.RoleAccessUpdateData,
      PostAPIResponse: state.DriverReducer.PostDataMessage,
    })
  );
  const  roleAccessSingleArray= useSelector((state) => ({ roleAccessSingleArray: state.Login.RoleAccessUpdateData,
    })
  );
  

  useEffect(() => {
    const locationPath = history.location.pathname
    let isUserAcc = roleAccessSingleArray.find((inx) => {
      return (`/${inx.ActualPagePath}` === locationPath)
    })
    if (!(isUserAcc === undefined)) {
      setUserPageAccState(isUserAcc)
    }
  }, [roleAccessSingleArray])

  //  This UseEffect => Featch Modules List data  First Rendering
  useEffect(() => {
    dispatch(getMethodForDriverList());
  }, []);
  const tabelColumns = [
    {
      text: "Name",
      dataField: "Name",
      sort: true,
    },

    {
      text: "Date Of Birth",
      dataField: "DOB",
      sort: true,
    },
    {
      text: "Address",
      dataField: "Address",
      sort: true,
    },
    {
      text: "UID",
      dataField: "UID",
      sort: true,
    },

    // For Edit, Delete ,and View Button Common Code function
    listPageCommonButtonFunction({
      dispatchHook: dispatch,
      ButtonMsgLable: "DriverType",
      deleteName: "Name",
      userPageAccessState: userPageAccState,
      editActionFun: editDriverTypeId,
      deleteActionFun: delete_DriverType_ID
    })
  ];


  


    return (
      <React.Fragment>
        <CommonListPage redux={redux} tabelColumns={tabelColumns}></CommonListPage>
        </React.Fragment>
       )
}

export default DriverList;
