import PropTypes from "prop-types";
import React, { useEffect, useRef, useCallback, useState } from "react";

//Import Icons
import FeatherIcon from "feather-icons-react";

// //Import Scrollbar
import SimpleBar from "simplebar-react";

//Import images

//i18n
import { withTranslation } from "react-i18next";

// MetisMenu
import MetisMenu from "metismenujs";
import { useHistory, withRouter } from "react-router-dom";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getUserDetailsAction, roleAceessAction } from "../../store/auth/login/actions";
import { demoRolleAcess } from "./aaaa";
// import { roleAccData } from "./APIDEMO";

const SidebarContent = (props) => {
  const ref = useRef();
  const pathName = props.location.pathname;
  const dispatch = useDispatch();

  const {
    roleAccData,
    roleAccSingleArr
  } = useSelector((state) => ({
    roleAccData: state.Login.RoleData,
    afterLoginUserDetails: state.Login.afterLoginUserDetails,
    roleAccSingleArr: state.Login.RoleAccessUpdateData,
  }));

  useEffect(() => {
    if (roleAccData.length <= 0) {
      var role = JSON.parse(localStorage.getItem("roleId"))
      if (!(role === undefined) && !(role === null)) {
        var party = role.Party_id
        var employee = role.Employee_id;
        dispatch(roleAceessAction(party, employee))
      };
    }
  }, [])

  const activateParentDropdown = useCallback((item) => {

    item.classList.add("active")
    const parent = item.parentElement
    const parent2El = parent.childNodes[1]
    if (parent2El && parent2El.id !== "side-menu") {
      parent2El.classList.add("mm-show")
    }

    if (parent) {
      parent.classList.add("mm-active")
      const parent2 = parent.parentElement

      if (parent2) {
        parent2.classList.add("mm-show") // ul tag

        const parent3 = parent2.parentElement // li tag

        if (parent3) {
          parent3.classList.add("mm-active") // li
          parent3.childNodes[0].classList.add("mm-active") //a
          const parent4 = parent3.parentElement // ul
          if (parent4) {
            parent4.classList.add("mm-show") // ul
            const parent5 = parent4.parentElement
            if (parent5) {
              parent5.classList.add("mm-show") // li
              parent5.childNodes[0].classList.add("mm-active") // a tag
            }
          }
        }
      }
      scrollElement(item);
      return false
    }
    scrollElement(item);
    return false
  }, []);

  // Use ComponentDidMount and ComponentDidUpdate method symultaniously
  useEffect(() => {
    let pathName = props.location.pathname

    let userAcc = roleAccSingleArr.find((inx) => {
      return (`/${inx.ActualPagePath}` === pathName)
    })
    if (userAcc === undefined) {
      pathName = "Dashbord"
    }
    else if (!userAcc.RoleAccess_IsShowOnMenu) {
      let listPagePath = roleAccSingleArr.find((inx) => {
        return (inx.id === userAcc.RelatedPageID)
      })
      if (!(listPagePath === undefined)) {
        pathName = `/${listPagePath.ActualPagePath}`
      }
    }

    const initMenu = () => {

      new MetisMenu("#side-menu")
      let matchingMenuItem = null
      const ul = document.getElementById("side-menu")
      const items = ul.getElementsByTagName("a")
      for (let i = 0; i < items.length; ++i) {
        if (pathName === items[i].pathname) {
          matchingMenuItem = items[i]
          break
        }
      }
      if (matchingMenuItem) {
        activateParentDropdown(matchingMenuItem)
      }
    }

    initMenu()

  }, [pathName, activateParentDropdown, roleAccSingleArr])


  useEffect(() => {
    ref.current.recalculate()
  })

  function scrollElement(item) {
    if (item) {
      const currentPosition = item.offsetTop
      if (currentPosition > window.innerHeight) {
        ref.current.getScrollElement().scrollTop = currentPosition
      }
    }

  }

  return (
    <React.Fragment>
      <SimpleBar style={{ maxHeight: "100%" }} ref={ref}>
        <div id="sidebar-menu">
          <ul className="metismenu list-unstyled" id="side-menu">

            {roleAccData.map((item) => {
              return (
                <>
                  {/* <li>
                    <Link to="/dashboard" className="">
                      <FeatherIcon
                        icon="home"
                      />
                      <span>{props.t("Dashboard")}</span>
                    </Link>
                  </li> */}
                  <li >
                    <Link to="/#" className="has-arrow">
                      <FeatherIcon icon="grid" />
                      <span>{props.t(item.ModuleName)}</span>

                    </Link>
                    <ul className="sub-menu">
                      {item.ModuleData.map((index, j) => {
                        if (index.RoleAccess_IsShowOnMenu === true) {
                          return (
                            <li>
                              <Link to={{ pathname: `/${index.ActualPagePath}`, }} >{props.t(index.Name)}</Link>
                            </li>

                          )
                        }


                      })}
                    </ul>
                  </li>
                </>
              )
            })}

          </ul>
        </div>
      </SimpleBar>
    </React.Fragment>
  );
};

SidebarContent.propTypes = {
  location: PropTypes.object,
  t: PropTypes.any,
};

export default withTranslation()(withRouter(SidebarContent));
