import React, { useCallback, useEffect, useRef, useState, } from "react";
import {
    Card,
    CardBody,
    CardHeader,
    Col,
    Container,
    DropdownItem,
    DropdownMenu,
    DropdownToggle,
    FormGroup,
    Label,
    Row,
    UncontrolledDropdown,
} from "reactstrap";
import Breadcrumb from "../../../components/Common/Breadcrumb";
import Select from "react-select";
import { MetaTags } from "react-meta-tags";
import { AvField, AvForm, } from "availity-reactstrap-validation";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { CommonGetRoleAccessFunction } from "../../../components/Common/CommonGetRoleAccessFunction";

import { BreadcrumbShow, AlertState } from "../../../store/actions";
import { editPartyTypeSuccess, PostPartyTypeAPI, PostPartyTypeAPISuccess, updatePartyTypeID } from "../../../store/Administrator/PartyTypeRedux/action";
import { getDivisionTypesID } from "../../../store/Administrator/PartyRedux/action";
import MetisMenu from "metismenujs";
import SidebarContent from "../../../components/VerticalLayout/SidebarContent";
//Import Icons
import FeatherIcon from "feather-icons-react";

// //Import Scrollbar
import SimpleBar from "simplebar-react";
const PartyType = (props) => {

    const formRef = useRef(null);
    const dispatch = useDispatch();
    const history = useHistory()

    //*** "isEditdata get all data from ModuleID for Binding  Form controls
    let editDataGatingFromList = props.state;
    let propsPageMode = props.pageMode;

    //SetState  Edit data Geting From Modules List component
    const [EditData, setEditData] = useState([]);
    const [pageMode, setPageMode] = useState("save");
    const [userPageAccessState, setUserPageAccessState] = useState('');
    const [division_dropdown_Select, setDivision_dropdown_Select] = useState("");

    //Access redux store Data /  'save_ModuleSuccess' action data
    const {
        PostAPIResponse,
        DivisionTypes,
        RoleAccessModifiedinSingleArray,
        RoleAccessData } = useSelector((state) => ({
            PostAPIResponse: state.PartyTypeReducer.PostData,
            DivisionTypes: state.PartyMasterReducer.DivisionTypes,
            RoleAccessModifiedinSingleArray: state.Login.RoleAccessUpdateData,
            RoleAccessData: state.Login.RoleData,

        }));

    // userAccess useEffect
    useEffect(() => {
        let userAcc = undefined
        if ((editDataGatingFromList === undefined)) {

            let locationPath = history.location.pathname
            userAcc = RoleAccessModifiedinSingleArray.find((inx) => {
                return (`/${inx.ActualPagePath}` === locationPath)
            })
        }
        else if (!(editDataGatingFromList === undefined)) {
            let relatatedPage = props.relatatedPage
            userAcc = RoleAccessModifiedinSingleArray.find((inx) => {
                return (`/${inx.ActualPagePath}` === relatatedPage)
            })

        }
        if (!(userAcc === undefined)) {
            setUserPageAccessState(userAcc)
        }

    }, [RoleAccessModifiedinSingleArray])


    useEffect(() => {
        dispatch(getDivisionTypesID());
    }, [dispatch]);
    // This UseEffect 'SetEdit' data and 'autoFocus' while this Component load First Time.
    useEffect(() => {
        if (!(userPageAccessState === '')) { document.getElementById("txtName").focus(); }
        if (!(editDataGatingFromList === undefined)) {
            setEditData(editDataGatingFromList);
            setPageMode("edit");
            setDivision_dropdown_Select({
                value: editDataGatingFromList.DivisionType_id,
                label: editDataGatingFromList.DivisionTypeName
            })
            dispatch(editPartyTypeSuccess({ Status: false }))
            dispatch(BreadcrumbShow(editDataGatingFromList.Name))
        }
        else if (!(propsPageMode === undefined)) {
            setPageMode(propsPageMode)
        }
    }, [editDataGatingFromList, propsPageMode])

    useEffect(() => {
        if ((PostAPIResponse.Status === true) && (PostAPIResponse.StatusCode === 200) && !(pageMode === "dropdownAdd")) {
            setDivision_dropdown_Select('')
            dispatch(PostPartyTypeAPISuccess({ Status: false }))
            formRef.current.reset();
            if (pageMode === "dropdownAdd") {
                dispatch(AlertState({
                    Type: 1,
                    Status: true,
                    Message: PostAPIResponse.Message,
                }))
            }
            else {
                dispatch(AlertState({
                    Type: 1,
                    Status: true,
                    Message: PostAPIResponse.Message,
                    RedirectPath: '/PartyTypeList',

                }))
            }
        }
        else if ((PostAPIResponse.Status === true) && !(pageMode === "dropdownAdd")) {
            dispatch(PostPartyTypeAPISuccess({ Status: false }))
            dispatch(AlertState({
                Type: 4,
                Status: true,
                Message: JSON.stringify(PostAPIResponse.Message),
                RedirectPath: false,
                AfterResponseAction: false
            }));
        }
    }, [PostAPIResponse])


    const DivisionTypesValues = DivisionTypes.map((Data) => ({
        value: Data.id,
        label: Data.Name
    }));

    function handllerDivisionTypes(e) {
        setDivision_dropdown_Select(e)
    }

    const FormSubmitButton_Handler = (event, values) => {
        const jsonBody = JSON.stringify({
            Name: values.Name,
            DivisionType: division_dropdown_Select.value,
            CreatedBy: 1,
            CreatedOn: "2022-07-18T00:00:00",
            UpdatedBy: 1,
            UpdatedOn: "2022-07-18T00:00:00"
        });

        if (pageMode === "edit") {
            dispatch(updatePartyTypeID(jsonBody, EditData.id));
        }
        else {
            dispatch(PostPartyTypeAPI(jsonBody));
        }
    };








    const ref = useRef();
    const pathName = props.location.pathname;
  
  
    // const  RoleAccessData=demoRolleAcess
    const {
      afterLoginUserDetails,
    } = useSelector((state) => ({

      afterLoginUserDetails: state.Login.afterLoginUserDetails,

    }));
 
  
    const activateParentDropdown = useCallback((item) => {
     
      item.classList.add("active")
      const parent = item.parentElement
      const parent2El = parent.childNodes[1]
      if (parent2El && parent2El.id !== "side-menu") {
        parent2El.classList.add("mm-show")
      }
  
      if (parent) {
        parent.classList.add("mm-active")
        const parent2 = parent.parentElement
  
        if (parent2) {
          parent2.classList.add("mm-show") // ul tag
  
          const parent3 = parent2.parentElement // li tag
  
          if (parent3) {
            parent3.classList.add("mm-active") // li
            parent3.childNodes[0].classList.add("mm-active") //a
            const parent4 = parent3.parentElement // ul
            if (parent4) {
              parent4.classList.add("mm-show") // ul
              const parent5 = parent4.parentElement
              if (parent5) {
                parent5.classList.add("mm-show") // li
                parent5.childNodes[0].classList.add("mm-active") // a tag
              }
            }
          }
        }
        scrollElement(item);
        return false
      }
      scrollElement(item);
      return false
    }, []);
  
    // Use ComponentDidMount and ComponentDidUpdate method symultaniously
    useEffect(() => {
      // debugger
      let pathName = props.location.pathname
  
      let userAcc = RoleAccessModifiedinSingleArray.find((inx) => {
        return (`/${inx.ActualPagePath}` === pathName)
      })
      if (userAcc === undefined) {
        return
      }
      else if (!userAcc.RoleAccess_IsShowOnMenu) {
        let listPagePath = RoleAccessModifiedinSingleArray.find((inx) => {
          return (inx.id === userAcc.RelatedPageID)
        })
        if (!(listPagePath === undefined)) {
          pathName = `/${listPagePath.ActualPagePath}`
        }
      }
  
      const initMenu = () => {
  
        new MetisMenu("#side-menu")
        let matchingMenuItem = null
        const ul = document.getElementById("side-menu")
        const items = ul.getElementsByTagName("a")
        for (let i = 0; i < items.length; ++i) {
          if (pathName === items[i].pathname) {
            matchingMenuItem = items[i]
            break
          }
        }
        if (matchingMenuItem) {
          activateParentDropdown(matchingMenuItem)
        }
      }
      initMenu()
    }, [pathName, activateParentDropdown, RoleAccessModifiedinSingleArray])
  
    useEffect(() => {
      ref.current.recalculate()
    })
  
    function scrollElement(item) {
      if (item) {
        const currentPosition = item.offsetTop
        if (currentPosition > window.innerHeight) {
          ref.current.getScrollElement().scrollTop = currentPosition - 300
        }
      }
    }
  
    

    const test = () => {


        return (
            <UncontrolledDropdown>
                <DropdownToggle type="button" className="btn btn-info">
                    Info <i className="mdi mdi-chevron-down"></i></DropdownToggle>
                <DropdownMenu className="dropdownmenu-info">
                    {/* <ul class="metismenu" id="example">

                        {arrData.map((index1) => {
                            return (
                                <li >
                                    <DropdownItem to="#">{index1.Name}</DropdownItem>


                                    <ul className="sub-menu">
                                        {index1.arrData.map((index2, j) => {

                                            return (
                                                <li>
                                                    <DropdownItem to="#">{index2.Name}</DropdownItem>

                                                    <ul className="sub-menu1">
                                                        {index2.arrData.map((index3, j) => {
                                                            return (
                                                                <li>
                                                                    <DropdownItem to="#">{index3.Name}</DropdownItem>
                                                                    <ul className="sub-menu">
                                                                        {index3.arrData.map((index4, j) => {
