import Tree from "../PartyPages/Tree";

export const formValid = ({ isError, required, hasValid, fieldLabel }, func) => {

    let isValid = false;
    Object.values(isError).forEach(val => {
        if ((val.length >= 0)) {
            isValid = false
        } else {
            isValid = true
        }
    });

    Object.keys(required).forEach((lab) => {

        if ((isError[lab].length >= 0)) {
            isError[lab] = hasValid[lab].inValidMsg
            isValid = false
            func({
                isError,
                hasValid,
                required,
                fieldLabel
            })
        } else {
            isValid = true
        }
    });
    return isValid
};


export const formValChange = (e, state, setFunc) => {
  
    e.preventDefault();
    const { name, value } = e.target;
    let isError = { ...state.isError };
    let hasValid = { ...state.hasValid };
    let required = { ...state.required };
    let fieldLabel = { ...state.fieldLabel };

    const regExp = RegExp(hasValid[name].regExp)
    isError[name] = regExp.test(value)
        ? ""
        : hasValid[name].inValidMsg

    setFunc({
        isError,
        hasValid,
        required,
        fieldLabel,

    })
};


 export function comAddPageFieldFunc(state,setState) {

    var inState = { ...state }
    const filed = { ...state.value }
    PageFieldMaster.forEach(ele => {

        Object.keys(filed).forEach(lab => {
            if (lab === ele.controlId) {
                inState.fieldLabel[lab] = ele.fieldLabel
                inState.hasValid[lab].regExp = ele.regExp
                inState.hasValid[lab].inValidMsg = ele.inValidMsg
                if (ele.isCompulsory) {
                    inState.required[lab] = true
                }
            }
        });
    });
    setState(inState)

}

export const PageFieldMaster = [
    {
        controlId: 'name',
        ControlType: 2,
        ControlTypeName: "Dropdown",
        fieldLabel: "Name ",
        isCompulsory: true,
        defaultSort: true,
        showInListPage: true,
        showInDownload: true,
        downloadDefaultSelect: false,
        regExp: /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/,
        inValidMsg: "Email is invalid",
    },
    {
        controlId: 'address',
        ControlType: 2,
        ControlTypeName: "text",
        fieldLabel: "Address ",
        isCompulsory: true,
        defaultSort: true,
        showInListPage: true,
        showInDownload: true,
        downloadDefaultSelect: false,
        regExp: /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/,
        inValidMsg: "Address  is invalid",
    },

    {
        controlId: 'uid',
        ControlType: 2,
        ControlTypeName: "text",
        fieldLabel: "UID ",
        isCompulsory: true,
        defaultSort: true,
        showInListPage: true,
        showInDownload: true,
        downloadDefaultSelect: false,
        regExp: /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/,
        inValidMsg: "UID  is invalid",
    },

]


