                                                                    <tr>
                                                                        {tableHederList.map((indx) => {
                                                                            return <th>{indx.text}</th>
                                                                        })}
                                                                    </tr>
                                                                </thead>
                                                                <tbody>

                                                                    {tableListData.map((indx, key) => {
                                                                        return (
                                                                            <tr>
                                                                                <td>
                                                                                    {indx.ModuleName}
                                                                                    <input
                                                                                        type="hidden"
                                                                                        id={"ModuleID" + key}
                                                                                        name={"ModuleID" + key}
                                                                                        value={indx.ModuleID}
                                                                                    />
                                                                                    <input
                                                                                        type="hidden"
                                                                                        id={"ID" + key}
                                                                                        name={"ID" + key}
                                                                                        value={indx.ID}
                                                                                    />
                                                                                    <input
                                                                                        type="hidden"
                                                                                        id={"RelatedPageID" + key}
                                                                                        value={indx.RelatedPageID}
                                                                                    />
                                                                                </td>
