                                                                                        </div>
                                                                                    </div>
                                                                                </td>
                                                                                {
                                                                                    PageAccess.map((indexPage) => {
                                                                                        if (indexPage.Name === "IsShowOnMenu") {
                                                                                            return (
                                                                                                <td className="text-center">
                                                                                                    {indx[`PageAccess_${indexPage.Name}`] ?
                                                                                                        <Row>
                                                                                                            <Col md={6}>
                                                                                                                <Input type={"checkbox"} id={indexPage.Name + key}
                                                                                                                    onChange={(e) => input_checkBoxHandler(indexPage.Name, key, e)}
                                                                                                                    defaultChecked={indx[`RoleAccess_${indexPage.Name}`] > 0 ? true : false} />
                                                                                                            </Col>

                                                                                                            <Col md={6}>
                                                                                                                <Input type={"checkbox"} id={indexPage.Name + key}
                                                                                                                    onChange={(e) => input_checkBoxHandler(indexPage.Name, key, e)}
                                                                                                                    defaultChecked={indx[`RoleAccess_${indexPage.Name}`] > 0 ? true : false} />
                                                                                                            </Col>

                                                                                                        </Row>
                                                                                                        : <>
                                                                                                            <Input type={"hidden"} id={indexPage.Name + key} />
                                                                                                            <Input type={"hidden"} id={indexPage.Name + key} />
                                                                                                        </>
                                                                                                    }

                                                                                                </td>
                                                                                            )
                                                                                        }
                                                                                        else {
                                                                                            return (
                                                                                                <td className="text-center">
                                                                                                    {indx[`PageAccess_${indexPage.Name}`] ?
                                                                                                        <Input type={"checkbox"} id={indexPage.Name + key}
                                                                                                            onChange={(e) => input_checkBoxHandler(indexPage.Name, key, e)}
                                                                                                            defaultChecked={indx[`RoleAccess_${indexPage.Name}`] > 0 ? true : false} />
                                                                                                        : <Input type={"hidden"} id={indexPage.Name + key} />
                                                                                                    }

                                                                                                </td>
                                                                                            )
                                                                                        }
                                                                                    })
                                                                                }


                                                                            </tr>
                                                                        )
                                                                    })}



                                                                </tbody>
                                                            </table>


                                                        </> :
                                                        <>
                                                            <br></br>
                                                            <br></br>
                                                            <br></br>
                                                            <br></br>
                                                            <br></br>
                                                            <br></br>
                                                            <br></br>
                                                            <br></br>
                                                        </>
                                                    }

                                                </CardBody>
                                            </>
                                    }
                                    <table class="table-bordered">
                                        <col />
                                        <col />
                                        <col />
                                        <colgroup span="4"></colgroup>
                                        <col />
                                        <tr>
                                            <th rowspan="2" style={{verticalAlign: "middle",textAlign: "center"}}>
                                                S.No.
                                            </th>
                                            <th rowspan="2" style={{verticalAlign: "middle",textAlign: "center"}}>Item</th>
                                            <th rowspan="2" style={{verticalAlign: "middle",textAlign: "center"}}>
                                                Description
                                            </th>
                                            <th
                                                colspan="3"
                                                // style="horizontal-align: middle; text-align: center; width: 50%"
                                                style={{horizontalAlign: "middle",textAlign: "center",width:"50%"}}
                                            >
                                                Items
                                            </th>
                                            <th rowspan="2" style={{verticalAlign: "middle",textAlign: "center"}}>
                                                Rejected Reason
                                            </th>
                                        </tr>
                                        <tr>
                                            <th scope="col">Order</th>
                                            <th scope="col">Received</th>
                                            <th scope="col">Accepted</th>
                                        </tr>
                                        <tr>
                                            <td>1</td>
                                            <td>Watch</td>
                                            <td>Analog</td>
                                            <td>100</td>
                                            <td>75</td>
                                            <td>25</td>
                                            <td>Not Functioning</td>
                                        </tr>
                                        <tr>
                                        <td>2</td>
                                            <td>Pendrive</td>
                                            <td>5GB</td>
                                            <td>250</td>
                                            <td>165</td>
                                            <td>85</td>
                                            <td>Not Working</td>
                                        </tr>
                                    </table>
                                </CardBody>
                            </Card>
                        </Card>
                    </Container>
                </div>
            </React.Fragment >
        );
    }
    else {
        return (
            <React.Fragment></React.Fragment>
        )
    }
};
