import React, { useEffect, useRef, useState } from "react";
import {
    Button,
  Card,
  CardBody,
  CardHeader,
  Col,
  Container,
  FormGroup,
  Label,
  Row,
} from "reactstrap";

import Select from "react-select";
import { MetaTags } from "react-meta-tags";

import Breadcrumb from "../../../components/Common/Breadcrumb";
import { AvField, AvForm,AvInput, } from "availity-reactstrap-validation";

import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";

import { BreadcrumbShow,AlertState } from "../../../store/actions";
import { postPriceListData, postPriceListDataSuccess } from "../../../store/Administrator/PriceList/action";
import { getPartyTypes } from "../../../store/Administrator/PartyRedux/action";

const PriceListMaster = (props) => {
  const formRef = useRef(null);
  const dispatch = useDispatch();
  const history = useHistory();

  //*** "isEditdata get all data from ModuleID for Binding  Form controls
  let editDataGatingFromList = props.state;
  let propsPageMode = props.pageMode;

  //SetState  Edit data Geting From Modules List component
  const [EditData, setEditData] = useState([]);
  const [pageMode, setPageMode] = useState("save");
  const [userPageAccessState, setUserPageAccessState] = useState("");
  const [partyType_dropdown_Select, setPartyType_dropdown_Select] = useState("");

  //Access redux store Data /  'save_ModuleSuccess' action data

  const { PostAPIResponse, PartyTypes, RoleAccessModifiedinSingleArray } =
    useSelector((state) => ({
      PostAPIResponse: state.PriceListReducer.PostData,
      PartyTypes: state.PartyMasterReducer.PartyTypes,
      //   RoleAccessModifiedinSingleArray: state.Login.RoleAccessUpdateData,
    }));

    useEffect(() => {
        dispatch(getPartyTypes());
    }, [dispatch]);


    const PartyTypeDropdown_Options= PartyTypes.map((Data) => ({
        value: Data.id,
        label: Data.Name
    }));

    function PartyType_Dropdown_OnChange_Handller(e) {
        setPartyType_dropdown_Select(e)
      
    }
    useEffect(() => {
        if ((PostAPIResponse.Status === true) && (PostAPIResponse.StatusCode === 200)&&!(pageMode==="dropdownAdd")) {
            // setpartyType_dropdown_Select('')
            dispatch(postPriceListDataSuccess({ Status: false }))
            formRef.current.reset();
            if (pageMode === "dropdownAdd") {
                dispatch(AlertState({
                    Type: 1,
                    Status: true,
                    Message: PostAPIResponse.Message,
                }))
            }
            else {
                dispatch(AlertState({
                    Type: 1,
                    Status: true,
                    Message: PostAPIResponse.Message,
                    RedirectPath: '',

                }))
            }
        }
        else if ((PostAPIResponse.Status === true) && !(pageMode==="dropdownAdd")) {
            dispatch(postPriceListDataSuccess({ Status: false }))
            dispatch(AlertState({
                Type: 4,
                Status: true,
                Message: JSON.stringify(PostAPIResponse.Message),
                RedirectPath: false,
                AfterResponseAction: false
            }));
        }
    }, [PostAPIResponse])


    

    const FormSubmitButton_Handler = (event, values) => {
        const jsonBody = JSON.stringify({
            Name: values.Name,
            IsSCM: values.IsSCM,
            IsDivision: values.IsDivision,
            CreatedBy: 1,
            CreatedOn: "2022-07-18T00:00:00",
            UpdatedBy: 1,
            UpdatedOn: "2022-07-18T00:00:00"
        });
            dispatch(postPriceListData(jsonBody));
    };
  

// IsEditMode_Css is use of module Edit_mode (reduce page-content marging)
var IsEditMode_Css = ''
if ((pageMode === "edit")||(pageMode==="copy")||(pageMode==="dropdownAdd")) { IsEditMode_Css = "-5.5%" };


        return (
            <React.Fragment>
                <div className="page-content" style={{ marginTop: IsEditMode_Css }}>
                    <MetaTags>
                        <title>PartyType| FoodERP-React FrontEnd</title>
                    </MetaTags>
                    <Breadcrumb breadcrumbItem={userPageAccessState.PageHeading} />
                    <Container fluid>
                        <Card className="text-black">
                            <CardHeader className="card-header   text-black" style={{ backgroundColor: "#dddddd" }} >
                                <h4 className="card-title text-black">{userPageAccessState.PageDescription}</h4>
                                <p className="card-title-desc text-black">{userPageAccessState.PageDescriptionDetails}</p>
                            </CardHeader>

                            <CardBody className=" vh-10 0 text-black" style={{ backgroundColor: "#whitesmoke" }} >
                                <AvForm onValidSubmit={(e, v) => { FormSubmitButton_Handler(e, v) }}
                                    ref={formRef}
                                >
                                    <Row className="">
                                        <Col md={12}>
                                            <Card style={{ backgroundColor: "whitesmoke" }}>
                                              
                                                
                                                    <CardHeader className="card-header   text-black " style={{ backgroundColor: "#dddddd" }} >
                                                    <Row className="mt-3">
                                                        <Col md="4">

                                                            <FormGroup className="mb-3 row ">
                                                                <Label className="col-sm-3 p-2 ml-n4 ">Party Type</Label>
                                                                <Col md="9">
                                                                    <Select
                                                                        value={partyType_dropdown_Select}
                                                                        options={PartyTypeDropdown_Options}
                                                                        className="rounded-bottom"
                                                                        placeholder="select"
                                                                        onChange={(e) => { PartyType_Dropdown_OnChange_Handller(e) }}
                                                                        classNamePrefix="select2-selection"

                                                                    />
                                                                </Col>
                                                            </FormGroup>
                                                        </Col>

                                                        <Col md="3" className="mt- ">
                                                            <Button type="button" color="primary" onClick={() => {  }}>Go</Button>
                                                        </Col>

                                                    </Row>
                                                </CardHeader>
                                                      
                                                        <FormGroup>
                                                            <Row>
                                                                <Col sm={2}>
                                                                    <div>
                                                                        {
                                                                            pageMode === "edit" ?
                                                                                userPageAccessState.RoleAccess_IsEdit ?
                                                                                    <button
                                                                                        type="submit"
                                                                                        data-mdb-toggle="tooltip" data-mdb-placement="top" title="Update Party Type"
                                                                                        className="btn btn-success w-md"
                                                                                    >
                                                                                        <i class="fas fa-edit me-2"></i>Update
                                                                                    </button>
                                                                                    :
                                                                                    <></>
                                                                                : (
                                                                                    userPageAccessState.RoleAccess_IsSave ?
                                                                                        <button
                                                                                            type="submit"
                                                                                            data-mdb-toggle="tooltip" data-mdb-placement="top" title="Save Party Type"
                                                                                            className="btn btn-primary w-md"
                                                                                        > <i className="fas fa-save me-2"></i> Save
                                                                                        </button>
                                                                                        :
                                                                                        <></>
                                                                                )
                                                                        }
                                                                    </div>
                                                                </Col>
                                                            </Row>
                                                        </FormGroup >
                                                  

                                                
                                            </Card>
                                        </Col>
                                    </Row>
                                </AvForm>
                            </CardBody>
                        </Card>

                    </Container>
                </div>
            </React.Fragment>
        )
    }
  
    
export default PriceListMaster
