import React, { useEffect, useState } from "react";
import Breadcrumbs from "../../../components/Common/Breadcrumb";
import { Col, Modal, Row } from "reactstrap";
import paginationFactory, {
  PaginationListStandalone,
  PaginationProvider,
} from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import BootstrapTable from "react-bootstrap-table-next";
import { useSelector, useDispatch } from "react-redux";
import "../../../assets/scss/CustomeTable/datatables.scss";
import DriverMaster from "../../../pages/Adminisrator/DriverPage/DriverMaster";
import { MetaTags } from "react-meta-tags";
import { useHistory } from "react-router-dom";
import {
  deleteDriverTypeIDSuccess,
  updateDriverTypeIDSuccess,
  getMethodForDriverList,
  editDriverTypeId,
  delete_DriverType_ID,
  PostMethod_ForDriverMasterSuccess,
} from "../../../store/Administrator/DriverRedux/action";
import { AlertState } from "../../../store/actions";
import { listPageCommonButtonFunction }
  from "../../../components/Common/CmponentRelatedCommonFile/listPageCommonButtons";





const CommonListPage = (props) => {

  const dispatch = useDispatch();
  const history = useHistory()

  const [modal_center, setmodal_center] = useState(false);

  const {
    editData,
    tableListData,
    updateResp,
    deleteResp,
    postResp,
  } = props.selector;

  const {tabelColumns,userPageAccState,getListAction,sortName,}=props
  

  //  This UseEffect => Featch Modules List data  First Rendering
  useEffect(() => {
    dispatch(getListAction());
  }, []);

  // This UseEffect => UpadateModal Success/Unsucces  Show and Hide Control Alert_modal
  useEffect(() => {

    if (updateResp.Status === true && updateResp.StatusCode === 200) {
      dispatch(updateDriverTypeIDSuccess({ Status: false }));
      dispatch(
        AlertState({
          Type: 1,
          Status: true,
          Message: updateResp.Message,
          AfterResponseAction: getMethodForDriverList,
        })
      );
      tog_center();
    } else if (updateResp.Status === true) {
      dispatch(updateDriverTypeIDSuccess({ Status: false }));
      dispatch(
        AlertState({
          Type: 3,
          Status: true,
          Message: JSON.stringify(updateResp.Message),
        })
      );
    }
  }, [updateResp]);


  useEffect(() => {
    if (deleteResp.Status === true && deleteResp.StatusCode === 200) {
      dispatch(deleteDriverTypeIDSuccess({ Status: false }));
      dispatch(
        AlertState({
          Type: 1,
          Status: true,
          Message: deleteResp.Message,
          AfterResponseAction: getMethodForDriverList,
        })
      );
    } else if (deleteResp.Status === true) {
      dispatch(deleteDriverTypeIDSuccess({ Status: false }));
      dispatch(
        AlertState({
          Type: 3,
          Status: true,
          Message: JSON.stringify(deleteResp.Message),
        })
      );
    }
  }, [deleteResp]);



  useEffect(() => {

    if ((postResp.Status === true) && (postResp.StatusCode === 200)) {
      dispatch(PostMethod_ForDriverMasterSuccess({ Status: false }))
      tog_center();
      dispatch(getMethodForDriverList());
      dispatch(AlertState({
        Type: 1,
        Status: true,
        Message: postResp.Message,
      }))
    }

    else if ((postResp.Status === true)) {
      dispatch(PostMethod_ForDriverMasterSuccess({ Status: false }))
      dispatch(AlertState({
        Type: 4,
        Status: true,
        Message: JSON.stringify(postResp.Message),
        RedirectPath: false,
        AfterResponseAction: false
      }));
    }


  }, [postResp])


  // Edit Modal Show When Edit Data is true
  useEffect(() => {
    if (editData.Status === true) {
      tog_center();
    }
  }, [editData]);

  function tog_center() {
    setmodal_center(!modal_center);
  }

  const defaultSorted = [
    {
      dataField: {sortName}, // if dataField is not match to any column you defined, it will be ignored.
      order: "asc", // desc or asc
    },
  ];

  const pageOptions = {
    sizePerPage: 10,
    totalSize: tableListData.length,
    custom: true,
  };

 
  if (!(userPageAccState === '')) {
    return (
      <React.Fragment>
        <MetaTags>
          <title>{userPageAccState.PageHeading}| FoodERP-React FrontEnd</title>
        </MetaTags>
        <div className="page-content">
          <PaginationProvider pagination={paginationFactory(pageOptions)}>
            {({ paginationProps, paginationTableProps }) => (
              <ToolkitProvider
                keyField="id"
                defaultSorted={defaultSorted}
                data={tableListData}
                columns={tabelColumns}
                search
              >
                {(toolkitProps) => (
                  <React.Fragment>
                    <Breadcrumbs
                      title={"Count :"}
                      breadcrumbItem={userPageAccState.PageHeading}
                      IsButtonVissible={(userPageAccState.RoleAccess_IsSave) ? true : false}
                      SearchProps={toolkitProps.searchProps}
                      breadcrumbCount={`Product Count: ${tableListData.length}`}
                      IsSearchVissible={true}
                      RedirctPath={`/DriverMaster`}
                      isExcelButtonVisible={true}
                      ExcelData={tableListData}
                    />
                    <Row>
                      <Col xl="12">
                        <div className="table-responsive">
                          <BootstrapTable
                            keyField={"id"}
                            responsive
                            bordered={false}
                            striped={false}
                            classes={"table  table-bordered"}
                            {...toolkitProps.baseProps}
                            {...paginationTableProps}
                          />
                        </div>
                      </Col>
                    </Row>
                    <Row className="align-items-md-center mt-30">
                      <Col className="pagination pagination-rounded justify-content-end mb-2">
                        <PaginationListStandalone {...paginationProps} />
                      </Col>
                    </Row>
                  </React.Fragment>
                )}
              </ToolkitProvider>
            )}
          </PaginationProvider>
          <Modal
            isOpen={modal_center}
            toggle={() => {
              tog_center();
            }}
            size="xl"
          >
            
            <DriverMaster state={editData.Data} relatatedPage={"/DriverMaster"} pageMode={editData.pageMode} />
          </Modal>
        </div>
      </React.Fragment>
    );
  }
  else {
    return (
      <React.Fragment></React.Fragment>
    )
  }
}

export default CommonListPage;
