                                return (
                                    <li >
                                        <Link to="/#" className="has-arrow">
                                            <FeatherIcon icon="grid" />
                                            <span>{item.ModuleName}</span>    {/* change Module Name */}

                                        </Link>
                                        <ul className="sub-menu">
                                            {item.ModuleData.map((index, j) => {
                                                if (index.RoleAccess_IsShowOnMenu === true) {
                                                    return (
                                                        <li>
                                                            <Link to={{ pathname: `/${index.ActualPagePath}`, }} >{(index.Name)}</Link>
                                                        </li>
                                                        // class={pathName === indx.SelectIcon ? "active" : ""}
                                                    )
                                                }
                                                else {
                                                    <></>
                                                }

                                            })}
                                        </ul>
                                    </li>
                                )
                            })}
                            {/* <li>
              <link href={"/RolesMaster"}>{props.t('Roles Master')}</link>
              <a href="/RolesList">Visit Pluralsight</a>
            </li>
            <li>
              <Link to={'/RolesList'}>{props.t('Roles List')}</Link>
            </li>
            <li>
              <Link to={'/validationTest'}>{props.t('validationTest')}</Link>
            </li> */}
                        </ul>
                    </div>
                </DropdownMenu>
            </UncontrolledDropdown>
        )
    }

    // IsEditMode_Css is use of module Edit_mode (reduce page-content marging)
    var IsEditMode_Css = ''
    if (pageMode === "edit" || pageMode == "dropdownAdd") { IsEditMode_Css = "-5.5%" };

    if (!(userPageAccessState === '')) {
        return (
            <React.Fragment>
                <div className="page-content" style={{ marginTop: IsEditMode_Css }}>
                    <MetaTags>
                        <title>PartyType| FoodERP-React FrontEnd</title>
                    </MetaTags>
                    <Breadcrumb breadcrumbItem={userPageAccessState.PageHeading} />
                    <SimpleBar style={{ maxHeight: "100%" }} ref={ref}>

